import React, { useState, useEffect, useRef } from "react";
import Img1 from "../../assets/img1.jpg";
import tutorialsdev from "../../assets/tutorialsdev.png";
import Input from "../../components/Input";
import { io } from "socket.io-client";

const Dashboard = () => {
  const [user, setUser] = useState(
    JSON.parse(localStorage.getItem("user:detail"))
  );
  const [conversations, setConversations] = useState([]);
  const [messages, setMessages] = useState({});
  const [message, setMessage] = useState("");
  const [users, setUsers] = useState([]);
  const [images, setImages] = useState([]);
  const [videos, setVideos] = useState([]);
  const [socket, setSocket] = useState(null);
  const messageRef = useRef(null);

  useEffect(() => {
    const newSocket = io("http://localhost:8080");
    setSocket(newSocket);
    return () => newSocket.close();
  }, []);

  useEffect(() => {
    if (socket) {
      socket.emit("addUser", user?.id);
      socket.on("getUsers", (users) => {
        console.log("activeUsers :>> ", users);
      });
      socket.on("getMessage", (data) => {
        setMessages((prev) => ({
          ...prev,
          messages: [
            ...prev.messages,
            { user: data.user, message: data.message },
          ],
        }));
      });
    }
  }, [socket, user]);

  useEffect(() => {
    messageRef?.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages?.messages]);

  useEffect(() => {
    const loggedInUser = JSON.parse(localStorage.getItem("user:detail"));
    const fetchConversations = async () => {
      const res = await fetch(
        `http://localhost:8000/api/conversations/${loggedInUser?.id}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      const resData = await res.json();
      setConversations(resData);
    };
    fetchConversations();
  }, []);

  useEffect(() => {
    const fetchUsers = async () => {
      const res = await fetch(`http://localhost:8000/api/users/${user?.id}`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      });
      const resData = await res.json();
      setUsers(resData);
    };
    fetchUsers();
  }, []);

  const fetchMessages = async (conversationId, receiver) => {
    const res = await fetch(
      `http://localhost:8000/api/message/${conversationId}?senderId=${user?.id}&&receiverId=${receiver?.receiverId}`,
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      }
    );
    const resData = await res.json();
    setMessages({ messages: resData, receiver, conversationId });
  };

  const handleFileUpload = async (event, type) => {
    const files = Array.from(event.target.files);
    if (files.length === 0) return;

    const formData = new FormData();
    files.forEach((file) => {
      formData.append("files", file);
    });

    try {
      // Upload the files
      const uploadRes = await fetch(`http://localhost:8000/api/upload`, {
        method: "POST",
        body: formData,
      });
      const uploadData = await uploadRes.json();

      // Update images or videos state based on type
      if (type === "image") {
        setImages((prev) => [...prev, ...files]);
      } else if (type === "video") {
        setVideos((prev) => [...prev, ...files]);
      }
    } catch (error) {
      console.error("Error uploading file:", error);
    }
  };

  const triggerFileInput = (type) => {
    document.querySelector(`#file-input-${type}`).click();
  };

  const sendMessage = async () => {
    if (!message && images.length === 0 && videos.length === 0) {
      return; // Do nothing if there's no message or files to send
    }

    const messageContent = {
      senderId: user?.id,
      receiverId: messages?.receiver?.receiverId,
      message: message || "", // Send the text message if present
      conversationId: messages?.conversationId,
      images, // Include images
      videos, // Include videos
    };

    try {
      // Emit message event to socket server
      socket.emit("sendMessage", messageContent);

      // Save message to the backend
      await fetch(`http://localhost:8000/api/message`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(messageContent),
      });

      // Clear the message and file states after sending
      setMessage("");
      setImages([]);
      setVideos([]);
    } catch (error) {
      console.error("Error sending message:", error);
    }
  };

  const displayImage = (filename) =>
    `http://localhost:8000/uploads/${filename}`;

  return (
    <div className="w-screen flex">
      <div className="w-[25%] h-screen bg-secondary overflow-scroll">
        <div className="flex items-center my-8 mx-14">
          <div>
            <img
              src={tutorialsdev}
              width={75}
              height={75}
              className="border border-primary p-[2px] rounded-full"
            />
          </div>
          <div className="ml-8">
            <h3 className="text-2xl">{user?.fullName}</h3>
            <p className="text-lg font-light">My Account</p>
          </div>
        </div>
        <hr />
        <div className="mx-14 mt-10">
          <div className="text-primary text-lg">Messages</div>
          <div>
            {conversations.length > 0 ? (
              conversations.map(({ conversationId, user }) => {
                return (
                  <div
                    className="flex items-center py-8 border-b border-b-gray-300"
                    key={conversationId}
                  >
                    <div
                      className="cursor-pointer flex items-center"
                      onClick={() => fetchMessages(conversationId, user)}
                    >
                      <div>
                        <img
                          src={Img1}
                          className="w-[60px] h-[60px] rounded-full p-[2px] border border-primary"
                        />
                      </div>
                      <div className="ml-6">
                        <h3 className="text-lg font-semibold">
                          {user?.fullName}
                        </h3>
                        <p className="text-sm font-light text-gray-600">
                          {user?.email}
                        </p>
                      </div>
                    </div>
                  </div>
                );
              })
            ) : (
              <div className="text-center text-lg font-semibold mt-24">
                No Conversations
              </div>
            )}
          </div>
        </div>
      </div>
      <div className="w-[50%] h-screen bg-white flex flex-col items-center">
        {messages?.receiver?.fullName && (
          <div className="w-[75%] bg-secondary h-[80px] my-14 rounded-full flex items-center px-14 py-2">
            <div className="cursor-pointer">
              <img src={Img1} width={60} height={60} className="rounded-full" />
            </div>
            <div className="ml-6 mr-auto">
              <h3 className="text-lg">{messages?.receiver?.fullName}</h3>
              <p className="text-sm font-light text-gray-600">
                {messages?.receiver?.email}
              </p>
            </div>
            <div className="cursor-pointer flex items-center">
              <img src={tutorialsdev} width={30} height={30} />
              <div className="ml-2">Logout</div>
            </div>
          </div>
        )}
        <div className="w-[75%] h-[70%] bg-white overflow-scroll">
          <div className="w-full p-14">
            {messages?.messages?.length > 0 ? (
              messages?.messages.map(
                ({ user, message, images, videos }, index) => (
                  <div
                    className={`flex items-center mb-4 ${
                      user?.id === user?.id ? "justify-end" : "justify-start"
                    }`}
                    key={index}
                  >
                    <div
                      className={`py-2 px-4 rounded-xl ${
                        user?.id === user?.id
                          ? "bg-primary text-white"
                          : "bg-gray-100 text-gray-800"
                      }`}
                    >
                      {message}
                    </div>
                    {images && images.length > 0 && (
                      <div className="flex flex-wrap">
                        {images.map((image, idx) => (
                          <img
                            key={idx}
                            src={displayImage(image.filename)}
                            alt={`Uploaded ${idx}`}
                            className="w-[100px] h-[100px] object-cover m-2"
                          />
                        ))}
                      </div>
                    )}
                  </div>
                )
              )
            ) : (
              <div className="text-center text-lg font-semibold mt-24">
                No messages
              </div>
            )}
            <div ref={messageRef} />
          </div>
        </div>
        <div className="w-[75%] flex justify-between items-center py-4 px-14 border-t border-t-gray-300">
          <input
            type="file"
            id="file-input-image"
            multiple
            style={{ display: "none" }}
            onChange={(e) => handleFileUpload(e, "image")}
          />
          <input
            type="file"
            id="file-input-video"
            multiple
            style={{ display: "none" }}
            onChange={(e) => handleFileUpload(e, "video")}
          />
          <button onClick={() => triggerFileInput("image")}>
            Upload Image
          </button>
          <button onClick={() => triggerFileInput("video")}>
            Upload Video
          </button>
          <Input
            placeholder="Type your message here"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && sendMessage()}
          />
          <button onClick={sendMessage}>Send</button>
        </div>
      </div>
      <div className="w-[25%] bg-gray-100"></div>
    </div>
  );
};

export default Dashboard;
