import { useEffect, useRef, useState } from "react";
import Img1 from "../../assets/img1.jpg";
import tutorialsdev from "../../assets/tutorialsdev.png";
import Input from "../../components/Input";
import { io } from "socket.io-client";

const Dashboard = () => {
  const [user, setUser] = useState(
    JSON.parse(localStorage.getItem("user:detail"))
  );
  const [conversations, setConversations] = useState([]);
  const [messages, setMessages] = useState({});
  const [message, setMessage] = useState("");
  const [users, setUsers] = useState([]);
  const [images, setImages] = useState([]);
  const [videos, setVideos] = useState([]);
  const [socket, setSocket] = useState(null);
  const messageRef = useRef(null);

  useEffect(() => {
    const newSocket = io("http://localhost:8080");
    setSocket(newSocket);
    return () => newSocket.close();
  }, []);

  useEffect(() => {
    if (socket) {
      socket.emit("addUser", user?.id);
      socket.on("getUsers", (users) => {
        console.log("activeUsers :>> ", users);
      });
      socket.on("getMessage", (data) => {
        setMessages((prev) => ({
          ...prev,
          messages: [
            ...prev.messages,
            { user: data.user, message: data.message },
          ],
        }));
      });
    }
  }, [socket, user]);

  useEffect(() => {
    messageRef?.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages?.messages]);

  useEffect(() => {
    const loggedInUser = JSON.parse(localStorage.getItem("user:detail"));
    const fetchConversations = async () => {
      const res = await fetch(
        `http://localhost:8000/api/conversations/${loggedInUser?.id}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      const resData = await res.json();
      setConversations(resData);
    };
    fetchConversations();
  }, []);

  useEffect(() => {
    const fetchUsers = async () => {
      const res = await fetch(`http://localhost:8000/api/users/${user?.id}`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      });
      const resData = await res.json();
      setUsers(resData);
    };
    fetchUsers();
  }, []);

  const fetchMessages = async (conversationId, receiver) => {
    const res = await fetch(
      `http://localhost:8000/api/message/${conversationId}?senderId=${user?.id}&&receiverId=${receiver?.receiverId}`,
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      }
    );
    const resData = await res.json();
    setMessages({ messages: resData, receiver, conversationId });
  };

  const handleFileUpload = async (event, type) => {
    const files = Array.from(event.target.files);
    if (files.length === 0) return;

    const formData = new FormData();
    files.forEach((file) => {
      formData.append("files", file);
    });

    try {
      // Upload the files
      const uploadRes = await fetch(`http://localhost:8000/api/upload`, {
        method: "POST",
        body: formData,
      });
      const uploadData = await uploadRes.json();

      // Send the file info in the chat message
      const fileInfo = uploadData.fileUrls; // Adjust based on your API response structure

      const messageContent = {
        senderId: user?.id,
        receiverId: messages?.receiver?.receiverId,
        message: JSON.stringify(fileInfo), // Assuming you send file URLs or info
        conversationId: messages?.conversationId,
      };

      socket.emit("sendMessage", messageContent);
      await fetch(`http://localhost:8000/api/message`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(messageContent),
      });

      // Clear the selected files after sending
      if (type === 'image') setImages([]);
      if (type === 'video') setVideos([]);

    } catch (error) {
      console.error("Error uploading file:", error);
    }
  };

  const triggerFileInput = (type) => {
    document.querySelector(`#file-input-${type}`).click();
  };

  return (
    <div className="w-screen flex">
      <div className="w-[25%] h-screen bg-secondary overflow-scroll">
        <div className="flex items-center my-8 mx-14">
          <div>
            <img
              src={tutorialsdev}
              width={75}
              height={75}
              className="border border-primary p-[2px] rounded-full"
            />
          </div>
          <div className="ml-8">
            <h3 className="text-2xl">{user?.fullName}</h3>
            <p className="text-lg font-light">My Account</p>
          </div>
        </div>
        <hr />
        <div className="mx-14 mt-10">
          <div className="text-primary text-lg">Messages</div>
          <div>
            {conversations.length > 0 ? (
              conversations.map(({ conversationId, user }) => {
                return (
                  <div
                    className="flex items-center py-8 border-b border-b-gray-300"
                    key={conversationId}
                  >
                    <div
                      className="cursor-pointer flex items-center"
                      onClick={() => fetchMessages(conversationId, user)}
                    >
                      <div>
                        <img
                          src={Img1}
                          className="w-[60px] h-[60px] rounded-full p-[2px] border border-primary"
                        />
                      </div>
                      <div className="ml-6">
                        <h3 className="text-lg font-semibold">
                          {user?.fullName}
                        </h3>
                        <p className="text-sm font-light text-gray-600">
                          {user?.email}
                        </p>
                      </div>
                    </div>
                  </div>
                );
              })
            ) : (
              <div className="text-center text-lg font-semibold mt-24">
                No Conversations
              </div>
            )}
          </div>
        </div>
      </div>
      <div className="w-[50%] h-screen bg-white flex flex-col items-center">
        {messages?.receiver?.fullName && (
          <div className="w-[75%] bg-secondary h-[80px] my-14 rounded-full flex items-center px-14 py-2">
            <div className="cursor-pointer">
              <img src={Img1} width={60} height={60} className="rounded-full" />
            </div>
            <div className="ml-6 mr-auto">
              <h3 className="text-lg">{messages?.receiver?.fullName}</h3>
              <p className="text-sm font-light text-gray-600">
                {messages?.receiver?.email}
              </p>
            </div>
            <div className="cursor-pointer">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className="icon icon-tabler icon-tabler-phone-outgoing"
                width="24"
                height="24"
                viewBox="0 0 24 24"
                strokeWidth="1.5"
                stroke="black"
                fill="none"
                strokeLinecap="round"
                strokeLinejoin="round"
              >
                <path stroke="none" d="M0 0h24v24H0z" fill="none" />
                <path d="M5 4h4l2 5l-2.5 1.5a11 11 0 0 0 5 5l1.5 -2.5l5 2v4a2 2 0 0 1 -2 2a16 16 0 0 1 -15 -15a2 2 0 0 1 2 -2" />
                <line x1="15" y1="9" x2="20" y2="4" />
                <polyline points="16 4 20 4 20 8" />
              </svg>
            </div>
          </div>
        )}
        <div className="h-[75%] w-full bg-white overflow-scroll">
          <div className="w-full p-14">
            {messages?.messages?.length > 0 ? (
              messages?.messages.map(({ user, message }, index) => {
                return (
                  <div
                    className={`flex items-center mb-4 ${
                      user?.id === user?.id
                        ? "justify-end"
                        : "justify-start"
                    }`}
                    key={index}
                  >
                    <div
                      className={`py-2 px-4 rounded-xl ${
                        user?.id === user?.id
                          ? "bg-primary text-white"
                          : "bg-gray-100 text-gray-800"
                      }`}
                    >
                      {message}
                    </div>
                    <div ref={messageRef} />
                  </div>
                );
              })
            ) : (
              <div className="text-center font-semibold text-lg mt-24">
                No Messages
              </div>
            )}
          </div>
        </div>
        {messages?.receiver?.fullName && (
          <div className="w-full flex items-center px-14 mt-6">
            <Input
              className="w-[75%]"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              placeholder="Type a message"
            />
            <button
              className="ml-8 py-4 px-8 rounded-full bg-primary shadow-sm text-white"
              onClick={sendMessage}
            >
              Send
            </button>
            <div className="ml-8">
              <input
                type="file"
                id="file-input-image"
                accept="image/*"
                onChange={(e) => handleFileUpload(e, 'image')}
                style={{ display: 'none' }}
              />
              <button
                className="py-2 px-4 rounded-full bg-secondary shadow-sm"
                onClick={() => triggerFileInput('image')}
              >
                Upload Image
              </button>
            </div>
            <div className="ml-8">
              <input
                type="file"
                id="file-input-video"
                accept="video/*"
                onChange={(e) => handleFileUpload(e, 'video')}
                style={{ display: 'none' }}
              />
              <button
                className="py-2 px-4 rounded-full bg-secondary shadow-sm"
                onClick={() => triggerFileInput('video')}
              >
                Upload Video
              </button>
            </div>
          </div>
        )}
        <div className="w-full flex flex-wrap p-14">
          {images.length > 0 &&
            images.map((image, index) => (
              <img
                key={index}
                src={URL.createObjectURL(image)}
                alt="Uploaded"
                className="w-[100px] h-[100px] m-2"
              />
            ))}
          {videos.length > 0 &&
            videos.map((video, index) => (
              <video
                key={index}
                src={URL.createObjectURL(video)}
                controls
                className="w-[200px] h-[150px] m-2"
              />
            ))}
        </div>
      </div>
      <div className="w-[25%] h-screen bg-secondary"></div>
    </div>
  );
};

export default Dashboard;
